/**
 * 滑动窗口的一般步骤：
 * 1、使用双指针，left 和 right； 使用while(right < strlen(s)){};
 * 2、一定要设置一个window。
 * 3、把符合条件的字符先放在window中，右移right
 * 4、直到所有的target都找到为止，停止右移。
 * 5、接下来，while(满足条件) ，在这个while中移动left。顺便把window中字符逐个移除window。
 * 6，在移除的过程中进行必要的信息处理。当不满足条件后，right再向右移。直到遍历完所有的字符。
 */
int findmax(int* nums, int k)
{   
    int max = INT_MIN;
    for (int i = 0; i < k; i++) {
        max = max > nums[i] ? max : nums[i];
    }
    return max;
}

int* maxSlidingWindow(int* nums, int numsSize, int k, int* returnSize){
    *returnSize = 0;
    int* res = (int*)malloc(sizeof(int) * (numsSize - k + 1));
    memset(res, 0, sizeof(int) * (numsSize - k + 1));
    int max = INT_MIN;
    int r = 0;
    int l = 0;
    int match = 0;
    int* window = (int*)malloc(sizeof(int) * k);
    memset(window, 0, sizeof(int) * k);
    while (r < numsSize) {
        window[match++] = nums[r];
        while (match == k) {
            res[(*returnSize)++] = findmax(window, k);
            for (int j = 0; j < k - 1; j++) {
                window[j] = window[j + 1];
            }
            l++;
            match--;
        }
        r++;
    }
    return res;
}

int maxTurbulenceSize1(int* A, int ASize){
    int l= 0;
    int r = 0;
    int max = INT_MIN;
    int flag = false;
    if (ASize == 1) {
        return 1;
    }
    if (A[0] > A[1]) {
        flag = true;
    }
    int valid = false;
    while(r < ASize - 1) {
        if (flag) { // 说明是偶数大与奇数
            if (r % 2 != 0) {
                r++;
                continue;
            }
            if ((A[r] > A[r + 1])) {
                valid = true;
            } else {
                valid = false;
            }
            r++; 
            if (valid == false) {
                max = max > (r - l + 1) ? max : r - l + 1;
                l++;
                if (A[l] > A[l + 1]) {
                    flag = true;
                } else {
                    flag = false;
                }
            }
        } else {
            if (r % 2 == 0) {
                r++;
                continue;
            }
            if ((A[r] < A[r + 1])) {
                valid = true;
            } else {
                valid = false;
            }   
            r++;
            if (valid == false) {
                max = max > (r - l + 1) ? max : r - l + 1;
                l++;
                if (A[l] > A[l + 1]) {
                    flag = true;
                } else {
                    flag = false;
                }
            }
        }
    }    
    return max;
}

int maxTurbulenceSize(int* A, int ASize){
    if (ASize == 1) {
        return 1;
    }
    char* releationship = (char*)malloc(sizeof(char) * (ASize - 1));
    memset(releationship, 0, sizeof(char) * (ASize - 1));
    for (int i = 0; i < ASize - 1; i++) {
        if (A[i] > A[i + 1]) {
            releationship[i] = 1;
        } else if (A[i] < A[i + 1]) {
            releationship[i] = -1;
        } else {
            releationship[i] = 0;
        }
    }
    int max = INT_MIN;
    int counter = 0;
    for (int j = 0; j < ASize - 2; j++) {
        if (releationship[j] * releationship[j + 1] < 0) {
            counter++;
            max = max > counter ? max : counter;
        } else {
            counter = 0;
        } 
    }
    return max;
}

int* findAnagrams(char * s, char * p, int* returnSize){
    *returnSize = 0;
    int hashNeeds[26] = {0};
    int hashWindon[26] = {0};
    int* res = (int*)malloc(sizeof(int) * 20100);
    memset(res, 0, sizeof(int) * 20100);

    for (int i = 0; i < strlen(p); i++) {
        hashNeeds[p[i] - 'a']++;
    }
    int charNumber = 0;
    for (int i = 0; i < 26; i++) {
        if (hashNeeds[i] != 0) {
            charNumber++;
        }
    }

    int left = 0;
    int right  = 0;
    int match = 0;
    int start = 0;
    while(right < strlen(s)) {
        char c1 = s[right];
        if (hashNeeds[c1 - 'a']) {
            hashWindon[c1 - 'a']++;
            if (hashNeeds[c1 - 'a'] == hashWindon[c1 - 'a']) {
                match++;
            }
        }
        right++;
        while (match == charNumber) {
            if (right - left == strlen(p)) {
                start = left;
                res[(*returnSize)++] = start;
            }
            char c2 = s[left];
            if (hashNeeds[c2 - 'a']) {
                hashWindon[c2 - 'a']--;
                if (hashNeeds[c2 - 'a'] > hashWindon[c2 - 'a']) {
                    match--;
                }
            }
            left++;
        }
    }
    return res;
}

int lengthOfLongestSubstring(char * s){
    int left = 0;
    int right =0;
    int hashWindow[255] = {0};
    int max = 0;
    
    while(right < strlen(s)) {
        char c1 = s[right];
        hashWindow[c1]++;
        right++;
        while (hashWindow[c1] > 1) { // 说明有重复的字符
            char c2 = s[left];
            hashWindow[c2]--;
            left++;
        }
        max = (right - left) > max ? (right - left) : max;
    }
    return max;
}

